arguments.argument:
dir+=random(120)-60;
if (distance>=maxdistance)
    instance_destroy();
image_single=0;
if (distance<maxdistance-1)
    move_contact_solid(dir,random(33)+10);
distance+=1;
if (!global.specialbake){
draw_set_color(make_color_rgb(255-distance*12,255-distance*12,255));
draw_line(xprev,yprev,x,y);
draw_line(xprev2,yprev2,xprev,yprev);
}
xprev2=xprev;
yprev2=yprev;
xprev=x;
yprev=y;
image_single=1;
if (!place_free(x,y))
    instance_destroy();
