arguments.argument:
if (other.stun)
    scr_assassinpain(1);
else{
    armor-=9*global.atmult;
    instance_create(other.x-3+random(7),other.y-3+random(7),obj_komatosplat);
    }
with (other){
    x+=(3-random(6));
    y+=(3-random(6));
    if (stun)
        (instance_create(x,y,obj_bullethit)).image_index=3;
    else
        instance_create(x,y,obj_bullethit);
    instance_destroy();
    }
