arguments.argument:
///Stop TAS execution/close the game

scr_tasunpause();
with (obj_tas) {
    if (is_executing) { 
        io_clear();
        room_speed = 30;
        rm_speed = 30;
        handle = true;
        ds_list_clear(held_keys);
        alarm[0] = -1;
        current_alarm = -1;
        is_executing = false;
        file_text_close(obj_tas.syncdata_log);
    }
    else {
        game_restart();
    }
}
